// EditorPage.js

import React, { useState, useRef, useEffect } from 'react';
import toast from 'react-hot-toast';
import ACTIONS from '../Actions';
import Client from '../components/Client';
import Editor from '../components/Editor';
import { useLocation, useNavigate, Navigate, useParams } from 'react-router-dom';
import { initSocket } from '../socket.js'; // Import the initSocket function from socket.js

const EditorPage = () => {
  const socketRef = useRef(null);
  const codeRef = useRef(null);
  const location = useLocation();
  const { roomId } = useParams();
  const reactNavigator = useNavigate();
  const [clients, setClients] = useState([]);
  const [editingPermission, setEditingPermission] = useState(true);
  const [isCreator, setIsCreator] = useState(false);

  useEffect(() => {
    const init = async () => {
      // Connect to the socket server with WebSocket transport
      socketRef.current = await initSocket('http://codesynchub.me/Codepulse/'); // Replace with your actual server URL

      socketRef.current.on('connect_error', (err) => handleErrors(err));
      socketRef.current.on('connect_failed', (err) => handleErrors(err));

      function handleErrors(e) {
        console.log('socket error', e);
        toast.error('Socket connection failed, try again later.');
        reactNavigator('/');
      }

      const username = location.state?.username;
      socketRef.current.emit(ACTIONS.JOIN, {
        roomId,
        username,
        editingPermission,
        isCreator: true,
      });

      socketRef.current.on(
        ACTIONS.JOINED,
        ({ clients, username, socketId, editingPermission, isCreator }) => {
          if (username && username !== location.state?.username) {
            toast.success(`${username} joined the room.`);
            console.log(`${username} joined`);
          }
          setClients(clients);
          setEditingPermission(editingPermission);
          setIsCreator(isCreator);
          socketRef.current.emit(ACTIONS.SYNC_CODE, {
            code: codeRef.current,
            socketId,
          });
        }
      );

      socketRef.current.on(
        ACTIONS.DISCONNECTED,
        ({ socketId, username }) => {
          toast.success(`${username} left the room.`);
          setClients((prev) => {
            return prev.filter(
              (client) => client.socketId !== socketId
            );
          });
        }
      );
    };

    init();

    return () => {
      socketRef.current.disconnect();
      socketRef.current.off(ACTIONS.JOINED);
      socketRef.current.off(ACTIONS.DISCONNECTED);
      // Add cleanup for other event listeners if necessary
    };
  }, [location.state, reactNavigator, roomId]);

  async function copyRoomId() {
    try {
      await navigator.clipboard.writeText(roomId);
      toast.success('Room ID has been copied to your clipboard');
    } catch (err) {
      toast.error('Could not copy the Room ID');
      console.error(err);
    }
  }

  function leaveRoom() {
    reactNavigator('/');
  }

  if (!location.state) {
    return <Navigate to="/" />;
  }

  return (
    <div className="editor-page">
      <aside className="sidebar">
        <div className="logo">
          <img className="logo-image" src="/icon.png" alt="logo" />
        </div>
        <h3>Connected Users</h3>
        <div className="clients-list">
          {clients.map((client) => (
            <Client key={client.socketId} username={client.username} />
          ))}
        </div>
        <div className="button-container">
          <button className="btn copy-btn" onClick={copyRoomId}>
            Copy ROOM ID
          </button>
          <button className="btn leave-btn" onClick={leaveRoom}>
            Leave
          </button>
        </div>
      </aside>
      <div className="editor-wrap">
        <Editor
          socketRef={socketRef}
          roomId={roomId}
          onCodeChange={(code) => {
            codeRef.current = code;
          }}
          isCreator={isCreator}
        />
      </div>
    </div>
  );
};

export default EditorPage;
